      }

      if (state.operatorValue) {
        if (state.nextValue) {
          return { nextValue: state.nextValue + button };
        }
        return { nextValue: button };
      } 

      if (state.nextValue) {
        const nextValue = state.nextValue === "0" ? button : state.nextValue + button;
        return {
	      nextValue,
