function useOperand(left, right, operation) {
  const pre = parseFloat(left || "0")
  const back = parseFloat(right || (operation === "/" || operation === '*' ? "1": "0"))
  if (operation === "+") {
    return (pre+back).toString();
  }
  if (operation === "-") {
    return (pre-back).toString();
  }
  if (operation === "*") {
    return (pre*back).toString();
  }
  if (operation === "/") {
    if (back == "0") {
      return "NaN";
    } 
    if (back != "0"){
      return (pre/back).toString();
    }
}   
  throw Error(`Unknown operation '${operation}'`);
}

function Calculate(State, button) {
    var num = "0123456789"
    if (button === "C") {
      return {
        result: "0",
        next: null,
        operation: null,
      };
    }

    if (num.indexOf(button) !== -1) {
      if (button === "0" && State.next === "0") {
        return {};
      }

      if (State.operation) { //연산자가 있다면
        if (State.next) {
          return { next: State.next + button };
        }
        return { next: button };
      }

      if (State.next) {
        const next = State.next === "0" ? button : State.next + button;
        return {
          next,
          result: null,
        };
      }
      return {
        next: button,
        result: null,
      };
    }

    if (button === ".") {
      if (State.next) {
        if (State.next.includes(".")) {
          return {};
        }
        return { next: State.next + "." };
      }
      return { next: "0." };
    }

    if (button === "=") {
      if (State.next && State.operation) {
        return {
          result: useOperand(State.result, State.next, State.operation),
          next: null,
          operation: null,
        };
      } 
    }

    if (State.operation) {
      return {
        result: useOperand(State.result, State.next, State.operation),
        next: null,
        operation: button,
      };
    }

    if (!State.next) {
      return { operation: button };
    }

    return {
      result: State.next,
      next: null,
      operation: button,
    };
  }

  export default calculate;
