import React, {Component } from 'react';
import Calculate from "./functionCalculate"
import Display from "./displayLayout"
import ButtonLayout from "./buttonLayout"

class calculator_layout extends Component{
    constructor(){
        super() 
        this.state = {
            next: null,
            operation: null,
            result: null
        }
    }

    buttonClick = (button_name) => {
        this.setState(Calculate(this.state, button_name))
    }

      handleKeyDown = (event) => {
    let { key } = event
    if (key === 'Enter')
      key = '='

    if ((/\d/).test(key)) {
      event.preventDefault()
      this.buttonClick(key)
    } else if (key === '.') {
      event.preventDefault()
      this.buttonClick(key)
    } 
   };  
  componentDidMount() {
    document.addEventListener('keydown', this.handleKeyDown)
  }

  componentWillUnmount() {
    document.removeEventListener('keydown', this.handleKeyDown)
  }


    render(){
        return(
            <div>
                <Display value = {this.state.next || this.state.result}></Display>
                <ButtonLayout click = {this.buttonClick}></ButtonLayout>
            </div>
        )
    }
}

export default calculator_layout;
