import React, { Component } from 'react';

class Display extends Component{
    constructor(props){
        super(props);
        this.state = {value: 0};
    }

    showDisplay = event => {
        this.setState({value: this.props.val});
    };

    render() {
        return(
            <input type="text" value={this.state.value} readOnly></input> //style="text-align:right"
        );
    }
}
export default Display;
