function inputKeyboard(event){
    let {key} = event
      const operations = ['/','*','+','-','.','=']

      if (key === 'Enter')
        key = '='

      if ((/\d/).test(key)) {
        event.preventDefault()
        this.buttonClick(key)
      } 
      if (operations.indexOf(key)!= -1) {
        event.preventDefault()
        this.buttonClick(key)
      } 
}

export default inputKeyboard
