import React, {Component } from 'react';
import Display from './Display';
import ButtonLayout from './ButtonLayout';
import Calculate from './Calculate'

class CalculatorLayout extends Component{
    constructor(){ //props
        super() //props
        this.state = {input: "0"}
    }

    buttonClick = (button_name) => {
        if(button_name === "C"){
            this.setState({input : "0"})
        }

        if(button_name === "="){
            this.setState(Calculate(this.state.input))
        }

        if(button_name.include('op')){
            this.setState({input : "999"})
        }
        //console.log(button_name)
    }

    render(){
        return(
            <div>
                <Display value = {this.state.input}></Display>
                <ButtonLayout click = {this.buttonClick}></ButtonLayout>
            </div>
        )
    }
}

export default CalculatorLayout;
