function Calculate(state,button_name){
    if(button_name=="C"){
        return {input:""}
    }

    if(button_name.includes("op")){
        operator = button_name.split('op_')[1]
        return {input:input, op:operator}
    }

    if(button_name=="."){
        return {input:input+button_name,op:"0"}
    }

    if(!isNaN(parseInt(button_name))){//when click the number button
      op = state.op  
      if(isNaN(parseInt(op))){ //op is operator not number
            input = parseFloat(input)
            //console.log(temp)
            //temp = temp.split(op)[0]
            //console.log(temp)
            switch(op){
                case "+":
                case "-":
                case "*":
                case "/":
            }
            //return {input:""}
        }
        return {input:input+button_name, op:"0"}
    }

}

export default Calculate;
