import React, {Component } from 'react';
import calculate from "./calculate"
import DisplayLayout from "./displayLayout"
import ButtonLayout from "./buttonLayout"
import inputKeyboard from './inputKeyboard'

class reactCalculator extends Component{
    constructor(){
        super() 
        this.state = {
            nextValue: null,
            operatorValue: null,
            displayValue: null
        }
    }

    buttonClick = (buttonName) => {
        this.setState(calculate(this.state, buttonName))
    }

    componentDidMount() {
      document.addEventListener('keydown', inputKeyboard)
    }

    componentWillUnmount() {
    document.removeEventListener('keydown', inputKeyboard)
    }

    render(){
        return(
            <div>
                <DisplayLayout value = {this.state.nextValue || this.state.displayValue}></DisplayLayout>
                <ButtonLayout click = {this.buttonClick}></ButtonLayout>
            </div>
        )
    }
}

export default reactCalculator;
