function Operate(left, right, operation) {
  const one = parseFloat(left || "0")
  const two = parseFloat(right || (operation === "/" || operation === '*' ? "1": "0"))
  if (operation === "+") {
    return (one+two).toString();
  }
  if (operation === "-") {
    return (one-two).toString();
  }
  if (operation === "*") {
    return (one*two).toString();
  }
  if (operation === "/") {
    if (two === "0") {
      return "NaN";
    } else {
      return (one/two).toString();
    }
  }
  throw Error(`Unknown operation '${operation}'`);
}

export default Operate;
