import React, {Component } from 'react';
import calculate from "../function/calculate"
import DisplayLayout from "./displayLayout"
import ButtonLayout from "./buttonLayout"
import clearOne from '../function/clearOne';

class reactCalculator extends Component{
    constructor(){
        super() 
        this.state = {
            nextValue: null,
            operatorValue: null,
            displayValue: null
        }
    }

    buttonClick = (buttonName) => {
        this.setState(calculate(this.state, buttonName))
    }

    handleKeyDown = (event) => {
      let {key} = event
      const operations = ['/','*','+','-','.','=']

      if(key === 'Backspace'){
        event.preventDefault()
        clearOne(this.state)
      }

      if (key === 'Enter')
        key = '='

      if ((/\d/).test(key)) {
        event.preventDefault()
        this.buttonClick(key)
      } 
      if (operations.indexOf(key)!= -1) {
        event.preventDefault()
        this.buttonClick(key)
      }
    };  

    componentDidMount() {
      document.addEventListener('keydown', this.handleKeyDown)
    }

    componentWillUnmount() {
    document.removeEventListener('keydown', this.handleKeyDown)
    }

    render(){
        return(
            <div>
                <DisplayLayout value = {this.state.nextValue || this.state.displayValue}></DisplayLayout>
                <ButtonLayout click = {this.buttonClick}></ButtonLayout>
            </div>
        )
    }
}

export default reactCalculator;
