import React, { Component } from 'react';

class Display extends Component{
    state = {
        value: 0
    };

    setValue = (val) => {
        this.setState({
            value: val
        })
    }

    render() {
        return(
            <input type="text" value={this.state.value} readOnly></input> //style="text-align:right"
        );
    }
}
export default Display;
