import doOperation from './doOperation'

function calculate(state, button) {
    var num = "0123456789"
    if (button === "C") {
      return {
        displayValue: "0", 
        nextValue: null,
        operatorValue: null,
      };
    }

    if (num.indexOf(button) !== -1) {
      if (button === "0" && state.nextValue === "0") {
        return {};
      }

      if (state.operatorValue) {
        if (state.nextValue) {
          return { nextValue: state.nextValue + button };
        }
        return { nextValue: button };
      } 

      if (state.nextValue) {
        const next = state.nextValue === "0" ? button : state.nextValue + button;
        return {
	        nextValue: next,
          displayValue: null, 
        };
      }
      return {
        nextValue: button,
        displayValue: null,
      };
    }

    if (button === ".") {
      if (state.nextValue) { 
        if (state.nextValue.includes(".")) {
          return {};
        }
        return { nextValue: state.nextValue + "." };
      }
      return { nextValue: "0." };
    }

    if (button === "=") {
      if (state.nextValue && state.operatorValue) {
        return {
          displayValue: doOperation(state),
          nextValue: null,
          operatorValue: null,
        };
      } 
    }

    if (state.operation) { 
      return {
        displayValue: doOperation(state),
        nextValue: null,
        operatorValue: button,
      };
    }

    if (!state.nextValue) {
      return { operatorValue: button };
    }

    return {
      displayValue: state.nextValue,
      nextValue: null,
      operatorValue: button,
    };
  }

  export default calculate;
