function Calculate(input,op,button_name){
    if(button_name=="C"){
        return {input:"clear"}
    }

    if(button_name.includes("op")){
        return {input:input, op:button_name.split('op_')[1]}
    }

    if(button_name=="."){
        return {input:input+button_name,next:"0"}
    }

    if(!isNaN(parseInt(button_name))){
        if(isNaN(parseInt(op))){
            switch(op){
                case "+":
                case "-":
                case "*":
                case "/":
            }
        }
        return {input:input + button_name, next:"0"}
    }

}

export default Calculate;
